import React from 'react'
import { FiStar } from 'react-icons/fi'
import { useTheme } from 'styled-components'
import * as S from './styles'

interface ProposalProps{
    name: string
    stars: number
    price: number
    comment?: string
    phone: string
}

const Star = ({isBlue}: {isBlue: boolean}) => {
    const {colors } = useTheme()

    return(
        <FiStar 
            color={isBlue ? colors.primary : colors.subtitle} 
            size={20}
        />
    )
}

const Proposal: React.FC<ProposalProps> = ({name, stars, comment, price, phone}) => {
    const {colors } = useTheme()

    return(
        <S.Container>
            <S.Header>
                <img src="data:image/png;base64," alt="avatar"/>
                <div>
                    <strong>{name}</strong>
                    <div>
                        <Star isBlue={stars >= 1}/>
                        <Star isBlue={stars >= 2}/>
                        <Star isBlue={stars >= 3}/>
                        <Star isBlue={stars >= 4}/>
                        <Star isBlue={stars >= 5}/>
                    </div>
                </div>
            </S.Header>
            <S.Info>
                <strong>Pre√ßo: </strong>
                <p>R${price}</p>
            </S.Info>
            <S.Info>
                <strong>Telefone: </strong>
                <p>{phone}</p>
            </S.Info>
            <S.Comment>
                {comment}
            </S.Comment>
        </S.Container>
    )
}

export default Proposal